<script setup>
import { useRouter } from "vue-router";
import { ref } from "vue";

const weekDays = ref(["ПН", "ВТ", "СР", "ЧЕТ", "ПТ", "СБ", "ВС"]);
const weekDates = ref([1, 2, 3, 4, 5, 6, 7]);
let currentWeek = ref(1);

const nextWeek = () => {
  currentWeek.value++;
};

const prevWeek = () => {
  if (currentWeek.value > 1) {
    currentWeek.value--;
  }
};

const router = useRouter();
const goToHumans = () => {
  router.push("/humans");
};
