    justify-content: center;
    align-items: center;
    margin-bottom: 10px;
    margin-top: 20px;

    img {
        max-width: 110px;
    }
}


.search-container {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 20px;
}


input[type="text"] {
    width: 250px;
    height: 38px;
    border-radius: 10px;
    border: none;
    background-color: #334B4B;
    color: white;
    font-size: 14px;
    display: inline-flex;
    padding: 12px 20px 12px 19px; // Reduced right padding
    align-items: center;
    gap: 16px;

    &::placeholder {
        color: white;
        opacity: 0.5;
    }

    &:focus {
        outline: none;
        box-shadow: 0 0 10px #3c3d3d;
    }

}
.links {
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-left: 24px;
}
.links a {
    margin-bottom: 10px;
    font-weight: bold;
    color: white;
    text-decoration: none;
    border-radius: 25px;
    padding: 10px;
    display: flex;
    align-items: center;
    gap: 10px;
  }
  
.links a.router-link-exact-active {
    width: 250px;
    color: black;
    background-color: #DDE144;
  }

  .notifications {
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
}

.notification {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    position: relative; 
    align-items: left;
    margin-bottom: 10px;
    margin-left: 24px;
    background-color: #334B4B;
    color: white;
    padding: 10px;
    border-radius: 25px;
    width: 250px;
    height: 175px;
    row-gap: 16px;
}

.info, .label, .status, .delivered, .time {
    font-family: 'Inter', sans-serif;
}

.info {
    font-weight: regular;
    font-size: 14px;
    color: #C1C9C9;
}

.label, .time {
    font-weight: medium;
    font-size: 16px;
}

.status, .delivered {
    font-weight: medium;
    font-size: 14px;
}

.status {
    width: fit-content;
    padding: 5px 10px;
    border-radius: 20px;
    margin-right: 10px;
}

.delivered {
    background-color: #555555;
    color: #DDE144;
}
button {
    position: absolute;
    align-items: center;
    right: 10px;
    bottom: 10px;
    border-radius: 50%;
    background-color: #DDE144;
    color: white;
    border: none;
    width: 60px;
    height: 60px;
    cursor: pointer;
}

